                />*/}
                <Text>{state.uploadProgress}%</Text>
              </>
            ) : (
              <>
                {/* {state.pdf && (
                  <Button title="Analyze CV" onPress={uploadAndAnalyze} />
                )} */}
                {state.stars > 0 && (
                  <Text style={styles.resultText}>
                    CV Quality: {getStarEmoji()}
                  </Text>
                )}
                {state.errorMessage && (
                  <Text style={styles.errorText}>{state.errorMessage}</Text>
                )}
              </>
            )}

            {/* ... rest of your code ... */}
          </View>
        </ScrollView>
      </SafeAreaView>
    </NavigationContainer>
  );
}

