/*
* 判断是否是 ios
*/
import { Dimensions, Platform } from "react-native";

export interface ESEToolAPI {

    /**
     * 是否是iOS平台
     */
    isIOS: boolean;

    /**
     * 是否是安卓平台
     */
    isAndroid: boolean;

    /**
     * 屏幕尺寸
     */
    screenSize: { width: number, height: number };
}

if (!global.se) {
    global.se = {};
}

export const isIOS = Platform.OS === 'ios';

export const isAndroid = Platform.OS === 'android';

export const screenSize = { width: Dimensions.get('window').width, height: Dimensions.get('window').height };