import React from 'react';

import {
    TextInput
} from 'react-native';

export class ETextInput extends React.Component{
    render() {
        return (
            <TextInput
                autoCapitalize={'none'}
                autoCorrect={false}
                spellCheck={false}
                allowFontScaling={false}
                underlineColorAndroid={'transparent'}
                {...this.props}/>
        )
    }
}