export interface EDatePickerViewOptions {
    /*提示语*/
    prompt: string,
    /*选中回调*/
    onSelectedValue: (value: string)=>void,
}

Object.assign(Component.prototype, {

    datePickerView() {

    },

    /**
     * alert
     * @param options DatePickerViewOptions
     */
    pickDate(options: DatePickerViewOptions) {
        this.setState({
            datePicker: {
                ...options,
                visible: true,
            }
        });
    }
});