import React from "react";

import PropTypes from 'prop-types';

import {
    View
} from 'react-native';

/**
 * EPage 中的每个页面的绘制，必须在 EPageItem 中绘制
 */
export default class EPageItem extends React.Component {

    static propTypes = {
        /**
         * 标签，对应 EPageBar 上的标签
         */
        label: PropTypes.string.isRequired
    };

    defaultStyle = {
        backgroundColor: 'white',
        flexDirection: 'column',
        flex: 1,
        alignItems: 'stretch',
        overflow: 'hidden'
    };

    render() {
        return (
            <View style={[ this.defaultStyle, this.props.style]}>
                {this.props.children}
            </View>
        )
    }
}