import React from 'react';

import {
    View
} from 'react-native';

/**
 * 横向布局，上下居中
 */
export class ERowCenter extends React.Component {
    render() {
        return (
            <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                {this.props.children}
            </View>
        )
    }
}