import React from 'react';

import PropTypes from 'prop-types';

import { ERowGeneral } from './ERowGeneral';

import { EImage } from '../react-native-element-core/EImage';

import { ColorPropType } from 'react-native';

import { ETheme } from '../react-native-element-core/theme';

/**
 * 从左向右布局依次为：icon-title-value-image
 */
export class ERowIconTitleValueImage extends React.Component {

    static propTypes = {
        ...ERowGeneral.propTypes,
        /**
         * 图片
         */
        image: PropTypes.any,
        /**
         * 图片 tintColor
         */
        imageTintColor: ColorPropType,
        /**
         * 图片css
         */
        imageStyle: PropTypes.any
    };

    static defaultProps = {
        arrowTintColor: 'black'
    };

    render() {
        return (
            <ERowGeneral
                iconStyle={[{ borderRadius: 3, marginLeft: ETheme.css.row.sideDistance, }, this.props.iconStyle]}
                valueStyle={[{ marginRight: ETheme.css.row.sideDistance }, this.props.valueStyle]}
                {...this.props}>
                {this.props.image &&
                <EImage
                    contentMode={'contain'}
                    tintColor={this.props.arrowTintColor}
                    style={[{ width: 20, height: 20, tintColor: this.props.tintColor, marginRight: ETheme.css.row.sideDistance }, this.props.imageStyle]}
                    slot={'custom'}
                    source={this.props.image}/>}
            </ERowGeneral>
        );
    }
}